INSERT INTO `class_exam_question` VALUES (1, 3, 1, '一元二次方程 ax²+bx+c=0 的求根公式是？', '[\"A. x = (-b±√(b²-4ac))/2a\",\"B. x = (b±√(b²-4ac))/2a\",\"C. x = (-b±√(b²+4ac))/2a\",\"D. x = (-b±√(b²-4ac))/a\"]', 'A. x = (-b±√(b²-4ac))/2a', '解析：这是一元二次方程的标准求根公式。', 2.00, 1, 1, 'admin', '2025-11-14 18:24:15', 'admin', '2025-11-14 18:27:48', '研究与开发实践');
INSERT INTO `class_exam_question` VALUES (3, 3, 1, '选择正确的时态：I ______ to the park yesterday.', '[\"A. go\",\"B. went\",\"C. have gone\",\"D. will go\"]', 'A. go', '解析：yesterday表示过去时间，应该用一般过去时。', 2.00, 1, 3, 'admin', '2025-11-14 18:25:45', 'admin', '2025-11-14 18:28:04', '研究与开发实践');
INSERT INTO `class_exam_question` VALUES (4, 3, 1, '\"Ubiquitous\"的意思是什么？', '[\"A. 独特的\",\"B. 无处不在的\",\"C. 稀有的\",\"D. 复杂的\"]', 'A. 独特的', '解析：Ubiquitous意为普遍存在的，无处不在的。', 2.00, 1, 4, 'admin', '2025-11-14 18:26:22', 'admin', '2025-11-14 18:28:11', '研究与开发实践');
INSERT INTO `class_exam_question` VALUES (5, 3, 1, '地球上最大的洲是？', '[\"A. 非洲\",\"B. 北美洲\",\"C. 亚洲\",\"D. 欧洲\"]', 'A. 非洲', '解析：亚洲面积约4458万平方公里，是最大的洲。', 1.00, 1, 5, 'admin', '2025-11-14 18:26:58', 'admin', '2025-11-14 18:28:21', '研究与开发实践');
INSERT INTO `class_exam_question` VALUES (6, 3, 1, '中国历史上第一个封建王朝是？', '[\"A. 夏朝\",\"B. 商朝\",\"C. 周朝\",\"D. 秦朝\"]', 'A. 夏朝', '解析：夏朝是中国史书中记载的第一个世袭制朝代。', 1.00, 1, 6, 'admin', '2025-11-14 18:27:35', '', '2025-11-14 18:27:35', '研究与开发实践');
